package nsdp

import (
	"testing"

	"github.com/stretchr/testify/require"
)

func TestStartStop(t *testing.T) {
	responder, err := NewTestResponder("127.0.0.1:63322")
	require.NoError(t, err)
	responder.AddResponses(
		"")
	err = responder.Start()
	require.NoError(t, err)
	err = responder.Stop()
	require.NoError(t, err)
}
